import React from 'react';
//import TodoList from'./TodoList.js';
function Form({inputText,setInputText,todo,setTodo}){

    function inputTextHandler(e){
        console.log(e.target.value);
        setInputText(e.target.value);

    }
    function submitHandler(e){
        e.preventDefault();
        setTodo([...todo,{text:inputText}]);
        setInputText('');

    }
    function deleteHandler(){
       setTodo(todo.filter((el)=>el.key!==todo.text));
    }


     return(
<div>

        <h1>Todo List</h1>
	<div className="container">
		<div className="addTodo">
			<input value={inputText} onChange={inputTextHandler} type="text" placeholder="Add todo"/>
			<button onClick={submitHandler}>ADD</button>
		</div>
        <ol className="notCompleted">
			<h3>NOT COMPLETED</h3>
            {todo.map (ele=><li key={todo}>{ele.text} 

                <button >done</button>
                <button onClick={deleteHandler}>delete</button></li>)}

		</ol>

		<ol className="Completed">
			<h3>COMPLETED</h3>

		</ol>


	</div>
</div>
 );
}
export default Form;
